import React, { useRef, useEffect } from 'react';
import { Message } from '../App';

interface ChatInterfaceProps {
  messages: Message[];
  isLoading: boolean;
}

export const ChatInterface: React.FC<ChatInterfaceProps> = ({ messages, isLoading }) => {
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="chat-interface">
      <h3>Conversation</h3>
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-state">
            <p>ðŸŽ¤ Start talking to begin your conversation!</p>
            <p>I'm here to help with any questions or concerns you might have.</p>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender === 'user' ? 'user' : 'assistant'}`}
            >
              <div className="message-content">
                <div className="message-text">{message.text}</div>
                <div className="message-time">{formatTime(message.timestamp)}</div>
              </div>
            </div>
          ))
        )}

        {isLoading && (
          <div className="message assistant">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <style jsx>{`
        .chat-interface {
          background: rgba(255, 255, 255, 0.8);
          border-radius: 15px;
          padding: 1.5rem;
          border: 1px solid rgba(102, 126, 234, 0.2);
          height: 400px;
          display: flex;
          flex-direction: column;
        }

        .chat-interface h3 {
          margin-bottom: 1rem;
          color: #333;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .messages-container {
          flex: 1;
          overflow-y: auto;
          display: flex;
          flex-direction: column;
          gap: 1rem;
          padding-right: 0.5rem;
        }

        .messages-container::-webkit-scrollbar {
          width: 6px;
        }

        .messages-container::-webkit-scrollbar-track {
          background: #f1f1f1;
          border-radius: 3px;
        }

        .messages-container::-webkit-scrollbar-thumb {
          background: #667eea;
          border-radius: 3px;
        }

        .empty-state {
          text-align: center;
          color: #666;
          padding: 2rem;
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: center;
          gap: 0.5rem;
        }

        .empty-state p:first-child {
          font-size: 1.1rem;
          font-weight: 500;
        }

        .empty-state p:last-child {
          font-size: 0.9rem;
          opacity: 0.8;
        }

        .message {
          display: flex;
          margin-bottom: 0.5rem;
        }

        .message.user {
          justify-content: flex-end;
        }

        .message.assistant {
          justify-content: flex-start;
        }

        .message-content {
          max-width: 70%;
          padding: 1rem;
          border-radius: 15px;
          position: relative;
        }

        .message.user .message-content {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          border-bottom-right-radius: 5px;
        }

        .message.assistant .message-content {
          background: #f8f9fa;
          color: #333;
          border: 1px solid #e9ecef;
          border-bottom-left-radius: 5px;
        }

        .message-text {
          margin-bottom: 0.5rem;
          line-height: 1.4;
          word-wrap: break-word;
        }

        .message-time {
          font-size: 0.75rem;
          opacity: 0.7;
          text-align: right;
        }

        .message.assistant .message-time {
          text-align: left;
        }

        .typing-indicator {
          display: flex;
          gap: 0.3rem;
          padding: 0.5rem;
        }

        .typing-indicator span {
          width: 8px;
          height: 8px;
          border-radius: 50%;
          background: #667eea;
          animation: typing 1.4s infinite ease-in-out;
        }

        .typing-indicator span:nth-child(1) {
          animation-delay: -0.32s;
        }

        .typing-indicator span:nth-child(2) {
          animation-delay: -0.16s;
        }

        @keyframes typing {
          0%, 80%, 100% {
            transform: scale(0.8);
            opacity: 0.5;
          }
          40% {
            transform: scale(1);
            opacity: 1;
          }
        }

        @media (max-width: 768px) {
          .chat-interface {
            padding: 1rem;
            height: 300px;
          }

          .message-content {
            max-width: 85%;
          }
        }
      `}</style>
    </div>
  );
};
