import React from 'react';
import { VoiceSettings as VoiceSettingsType } from '../App';

interface VoiceSettingsProps {
  settings: VoiceSettingsType;
  onSettingsChange: (settings: VoiceSettingsType) => void;
}

export const VoiceSettings: React.FC<VoiceSettingsProps> = ({ settings, onSettingsChange }) => {
  const handleVoiceChange = (voice: 'male' | 'female') => {
    onSettingsChange({ ...settings, voice });
  };

  const handleSpeedChange = (speed: number) => {
    onSettingsChange({ ...settings, speed });
  };

  const handlePitchChange = (pitch: number) => {
    onSettingsChange({ ...settings, pitch });
  };

  return (
    <div className="voice-settings">
      <h3>Voice Settings</h3>
      <div className="settings-grid">
        <div className="setting-group">
          <label>Voice Type:</label>
          <div className="voice-buttons">
            <button
              className={`voice-btn ${settings.voice === 'female' ? 'active' : ''}`}
              onClick={() => handleVoiceChange('female')}
            >
              ðŸ‘© Female
            </button>
            <button
              className={`voice-btn ${settings.voice === 'male' ? 'active' : ''}`}
              onClick={() => handleVoiceChange('male')}
            >
              ðŸ‘¨ Male
            </button>
          </div>
        </div>

        <div className="setting-group">
          <label>Speed: {settings.speed}x</label>
          <input
            type="range"
            min="0.5"
            max="2.0"
            step="0.1"
            value={settings.speed}
            onChange={(e) => handleSpeedChange(parseFloat(e.target.value))}
            className="slider"
          />
        </div>

        <div className="setting-group">
          <label>Pitch: {settings.pitch}x</label>
          <input
            type="range"
            min="0.5"
            max="2.0"
            step="0.1"
            value={settings.pitch}
            onChange={(e) => handlePitchChange(parseFloat(e.target.value))}
            className="slider"
          />
        </div>
      </div>

      <style jsx>{`
        .voice-settings {
          background: rgba(255, 255, 255, 0.8);
          border-radius: 15px;
          padding: 1.5rem;
          margin-bottom: 2rem;
          border: 1px solid rgba(102, 126, 234, 0.2);
        }

        .voice-settings h3 {
          margin-bottom: 1rem;
          color: #333;
          font-size: 1.2rem;
          font-weight: 600;
        }

        .settings-grid {
          display: grid;
          gap: 1.5rem;
        }

        .setting-group {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .setting-group label {
          font-weight: 500;
          color: #555;
          font-size: 0.9rem;
        }

        .voice-buttons {
          display: flex;
          gap: 0.5rem;
        }

        .voice-btn {
          flex: 1;
          padding: 0.8rem;
          border: 2px solid #e0e0e0;
          border-radius: 10px;
          background: white;
          cursor: pointer;
          transition: all 0.3s ease;
          font-size: 0.9rem;
        }

        .voice-btn:hover {
          border-color: #667eea;
          background: rgba(102, 126, 234, 0.05);
        }

        .voice-btn.active {
          border-color: #667eea;
          background: #667eea;
          color: white;
        }

        .slider {
          width: 100%;
          height: 6px;
          border-radius: 3px;
          background: #e0e0e0;
          outline: none;
          -webkit-appearance: none;
        }

        .slider::-webkit-slider-thumb {
          -webkit-appearance: none;
          appearance: none;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          background: #667eea;
          cursor: pointer;
        }

        .slider::-moz-range-thumb {
          width: 20px;
          height: 20px;
          border-radius: 50%;
          background: #667eea;
          cursor: pointer;
          border: none;
        }

        @media (max-width: 768px) {
          .voice-settings {
            padding: 1rem;
          }

          .voice-buttons {
            flex-direction: column;
          }
        }
      `}</style>
    </div>
  );
};
